interface LogoProps {
  color?: string;
  size?: 'x-small' | 'small' | 'default' | 'large' | 'x-large';
}

const aspectRatio = 941 / 305

const calculateDimensions = (width?: number, height?: number) => {
    if (width && height) {
        return { width, height }
    } else if (width) {
        return { width, height: width / aspectRatio }
    } else if (height) {
        return { width: height * aspectRatio, height }
    } else {
        return { width: 96, height: 31.12 }
    }
}

const sizeMap = {
    'x-small': 48,
    'small': 72,
    'default': 96,
    'large': 128,
    'x-large': 192,
    'xx-large': 256,
}

const getDimensionsBySize = (size: LogoProps['size']) => {
    return calculateDimensions(sizeMap[size!])
}

